sections.code:
div.flash.notice {
	background: url(../images/true.png) 62px 141px no-repeat;
	background-color: #81c784;
	border-color: #81c784;
	color: #fff;
}
* {
	border: 0px none !important;
}
div.details {
	background: #E9E1D9;
	border: 0px none;
}
body,
#wrapper,
#content,
#main.nosidebar #content,
#main,
#sidebar,
#footer {
	background-color: RGB(227,225,223) !important;
	background-image: none !important;
	border: 0px none;
	font-family: Ubuntu;
	font-size: 14px;
}

#top-menu {
    background: #24292e !important;
    box-shadow: none;
}

#top-menu a,
#top-menu #loggedas {
    color: rgba(227, 225, 223, 0.55);
    font-weight: 500;
}

.select2-container {
    display: none !important;
}

#sidebar,
#content,
#header {
	box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
}
#main-menu,
#header {
	background-image: none !important;
	background-color: #24292E !important;
	color: #C8C9CA;
}

#header {
	display: flex;
	flex-wrap: wrap;
	padding: 4px 8px 10px 6px;
}

div#quick-search {
    order: 3;
    flex: 0 3 auto;
    position: relative;
    z-index: 999;
    margin-left: auto;
    margin-top: 4px;
    margin-right: 8px;
}

#main-menu {
	flex: 0 1 auto;
  order: 2;
  width: auto;
  position: relative;
}

#main-menu li a {
	color: rgba(255, 255, 255, 0.75);
	text-shadow: none;
}
#main-menu li a:hover, .issues.selected {
	color: rgba(255, 255, 255, 0.95);
	text-decoration: none;
}
#header input,
#quick-search #q {
	min-height: 24px;
	padding: 6px 8px;
	font-size: 14px;
	line-height: 20px;
	color: #24292e;
	vertical-align: middle;
	background-color: rgba(255,255,255,0.125);
	background-repeat: no-repeat;
	background-position: right 8px center;
	border: 1px solid #d1d5da;
	border-radius: 3px;
	outline: none;
	box-shadow: inset 0 1px 2px rgba(27, 31, 35, 0.075);
}

#main-menu li a.selected {
background: none !important;
background-color: rgba(255,255,255,0.125) !important;
box-shadow: inset 0 1px 2px rgba(27, 31, 35, 0.075);
border: 1px solid #d1d5da;
border-radius: 3px;

}

table.list {
    position: relative;
    border: 1px solid rgba(0,0,0,.12) !important;
    border-collapse: collapse;
    white-space: nowrap;
    font-size: 14px;
    background-color: #fff;
		box-shadow: 0 2px 2px 0 rgba(0,0,0,.14), 0 3px 1px -2px rgba(0,0,0,.2), 0 1px 5px 0 rgba(0,0,0,.12);
}

table.list thead {
    padding-bottom: 3px;
}

table.list td:first-of-type, table.list th:first-of-type {
    padding-left: 24px;
}

table.list th {
    position: relative;
    vertical-align: bottom;
    text-overflow: ellipsis;
    font-weight: 700;
    line-height: 24px;
    letter-spacing: 0;
    height: 48px;
    font-size: 14px;
    color: rgba(0,0,0,.54);
    padding-bottom: 8px;
    box-sizing: border-box;
}

table.list th {
    padding: 0 18px 12px 18px;
/*     text-align: right; */
}

table.list tbody tr:hover {
    background-color: #eee;
}

table.list tbody tr {
    position: relative;
    height: 48px;
    transition-duration: .28s;
    transition-timing-function: cubic-bezier(.4,0,.2,1);
    transition-property: background-color;
}

table.list tbody td {
    position: relative;
    height: 48px;
    border-top: 1px solid rgba(0,0,0,.12) !important;
    border-bottom: 1px solid rgba(0,0,0,.12) !important;
    padding: 12px 18px;
    box-sizing: border-box;
		vertical-align: middle;
}

table.list tbody td {
/*     text-align: right; */
		padding: 0px;
}

[type="submit"], [onclick*="submitPreview"] {
	background-color: rgba(158,158,158,.2);
    box-shadow: 0 2px 2px 0 rgba(0,0,0,.14), 0 3px 1px -2px rgba(0,0,0,.2), 0 1px 5px 0 rgba(0,0,0,.12);
		border: none;
    border-radius: 2px;
    color: #000;
    position: relative;
    height: 36px;
    margin: 0;
    min-width: 64px;
    padding: 0 16px;
    display: inline-block;
    font-size: 14px;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0;
    overflow: hidden;
    will-change: box-shadow;
    transition: box-shadow .2s cubic-bezier(.4,0,1,1),background-color .2s cubic-bezier(.4,0,.2,1),color .2s cubic-bezier(.4,0,.2,1);
    outline: none;
    cursor: pointer;
    text-decoration: none;
    text-align: center;
    line-height: 36px;
    vertical-align: middle;
		font-family: Ubuntu;
}

[type="submit"]:focus:not(:active),  [onclick*="submitPreview"]:focus:not(:active) {
    box-shadow: 0 0 8px rgba(0,0,0,.18), 0 8px 16px rgba(0,0,0,.36);
    background-color: rgba(158,158,158,.4);
		text-decoration: none;
}

[type="submit"]:hover,  [onclick*="submitPreview"]:hover {
    background-color: rgba(158,158,158,.35);
		text-decoration: none;
}

div.thumbnails div {
    background-color: rgba(0,0,0,.03);
    border-radius: 2px;
    box-shadow: 0 0 1px rgba(0,0,0,.11), 0 1px 2px rgba(0,0,0,.22);
    box-sizing: border-box;
    color: #757575;
    -webkit-transition: background-color .15s cubic-bezier(.4,0,.2,1),box-shadow .15s cubic-bezier(.4,0,.2,1);
    transition: background-color .15s cubic-bezier(.4,0,.2,1),box-shadow .15s cubic-bezier(.4,0,.2,1);
}

div.thumbnails div:hover {
    box-shadow: 0 0 3px rgba(0,0,0,.13), 0 3px 6px rgba(0,0,0,.26);
}

#header > h1 {
	background: url(http://vignette2.wikia.nocookie.net/firststrikemod/images/f/f2/Rebelsymbol.png/revision/latest?cb=20071031231453) no-repeat;
	background-size: 36px;
	background-position: 14px;
	order: 1;
	flex: 2 0 calc(100% - 130px);
	margin: 0 0 30px 0;
}

div#content > h2, div#content h2.contacts_header, div#content > div.filters h2, div#content > #add-news > h2, div#content > #add-message > h2, div#content > .message.details > h2, div#content > .board.details > h2, div#content > #add-document > h2 {
	background: #E9E1D9;
}
